import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => (
  <div className="flex flex-col items-center justify-center min-h-[60vh] bg-white font-sans">
    <h1 className="text-4xl font-extrabold text-primary mb-4">SmartCart: Intelligent Pricing at Your Fingertips</h1>
    <p className="text-lg text-gray-600 mb-8">Shop smarter with real-time discounts and transparent pricing.</p>
    <Link to="/products" className="bg-primary text-white px-8 py-3 rounded-lg text-lg font-bold hover:bg-red-600 transition">Browse Products</Link>
  </div>
);

export default Home;
