import React from 'react';

const Toast = ({ message }) => {
  if (!message) return null;
  return (
    <div className="fixed bottom-6 right-6 z-50 px-4 py-2 rounded text-white shadow-lg animate-fade-in-up bg-green-600"
      role="alert"
      aria-live="assertive"
    >
      {message}
    </div>
  );
};

export default Toast;
