import React from 'react';

function calculateTax(category, price) {
  if (category === 'Electronics') return price * 0.10;
  if (category === 'Clothing') return price * 0.05;
  return 0;
}
function getLoyaltyDiscount(level) {
  if (level === 'Gold') return 0.15;
  if (level === 'Silver') return 0.10;
  if (level === 'Bronze') return 0.05;
  return 0;
}

const CartSummary = ({ cart, clearCart, loyaltyLevel, setLoyaltyLevel }) => {
  // Itemized calculation with item-specific discount and tax
  let itemized = [];
  let subtotal = 0;
  cart.forEach(item => {
    let base = item.price * item.quantity;
    let discount = 0;
    if (item.category === 'Electronics' && item.quantity > 2) {
      discount = base * 0.15;
      base -= discount;
    }
    const tax = calculateTax(item.category, base);
    const subtotalItem = base + tax;
    subtotal += subtotalItem;
    itemized.push({
      ...item,
      tax,
      subtotal: subtotalItem,
      itemDiscount: discount
    });
  });
  let bulkDiscount = subtotal > 200 ? subtotal * 0.10 : 0;
  let afterBulk = subtotal - bulkDiscount;
  let loyaltyDiscount = afterBulk * getLoyaltyDiscount(loyaltyLevel);
  let finalTotal = afterBulk - loyaltyDiscount;

  return (
    <div className="mt-4 p-4 bg-gray-50 rounded shadow">
      <div className="mb-1">Subtotal: <span className="font-semibold">${subtotal.toFixed(2)}</span></div>
      <div className="mb-1 text-green-700">Bulk Discount: -${bulkDiscount.toFixed(2)}</div>
      <div className="mb-1 text-green-700">Loyalty Discount: -${loyaltyDiscount.toFixed(2)}</div>
      <div className="font-bold text-lg mt-2">Final Total: ${finalTotal.toFixed(2)}</div>
      <button
        className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        onClick={clearCart}
      >
        Clear Cart
      </button>
      <div className="mt-4">
        <label className="font-semibold mr-2">Loyalty Level:</label>
        <select
          className="border rounded px-2 py-1"
          value={loyaltyLevel}
          onChange={e => setLoyaltyLevel(e.target.value)}
        >
          <option value="Bronze">Bronze</option>
          <option value="Silver">Silver</option>
          <option value="Gold">Gold</option>
        </select>
      </div>
    </div>
  );
};

export default CartSummary;
