import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { cart } = useCart();
  const { user, logout } = useAuth();
  const cartCount = cart.reduce((sum, item) => sum + item.quantity, 0);
  const navigate = useNavigate();

  return (
    <nav className="bg-white shadow sticky top-0 z-50 flex items-center justify-between px-8 py-4 font-sans">
      <span className="font-extrabold text-2xl text-primary">SmartCart</span>
      <div className="flex gap-6 items-center">
        <NavLink to="/" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Home</NavLink>
        <NavLink to="/products" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Products</NavLink>
        <NavLink to="/cart" className={({isActive}) => isActive ? 'text-primary font-bold flex items-center' : 'hover:text-primary flex items-center'}>
          Cart
          {cartCount > 0 && (
            <span className="ml-2 bg-primary text-white rounded-full px-2 py-0.5 text-xs font-bold animate-bounce" aria-label={`Cart has ${cartCount} items`}>
              {cartCount}
            </span>
          )}
        </NavLink>
        <NavLink to="/loyalty" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Loyalty</NavLink>
        {user && (
          <NavLink to="/orders" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Orders</NavLink>
        )}
        {!user ? (
          <>
            <NavLink to="/login" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Login</NavLink>
            <NavLink to="/register" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Register</NavLink>
          </>
        ) : (
          <button
            className="bg-primary text-white px-4 py-1 rounded hover:bg-red-600 font-semibold ml-2"
            onClick={() => { logout(); navigate('/'); }}
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
