import React, { createContext, useContext, useState } from 'react';
import api from '../services/axios';

const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => JSON.parse(localStorage.getItem('user')) || null);
  const [token, setToken] = useState(() => localStorage.getItem('token') || '');
  const [authLoading, setAuthLoading] = useState(false);
  const [authError, setAuthError] = useState('');

  const login = async (email, password) => {
    setAuthLoading(true);
    setAuthError('');
    try {
      const res = await api.post('/auth/login', { email, password });
      setUser(res.data.user);
      setToken(res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      localStorage.setItem('token', res.data.token);
      return true;
    } catch (err) {
      setAuthError(err.response?.data?.error || 'Login failed');
      return false;
    } finally {
      setAuthLoading(false);
    }
  };

  const register = async (name, email, password, loyaltyLevel) => {
    setAuthLoading(true);
    setAuthError('');
    try {
      const res = await api.post('/auth/register', { name, email, password, loyaltyLevel });
      setUser(res.data.user);
      setToken(res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      localStorage.setItem('token', res.data.token);
      return true;
    } catch (err) {
      setAuthError(err.response?.data?.error || 'Registration failed');
      return false;
    } finally {
      setAuthLoading(false);
    }
  };

  const logout = () => {
    setUser(null);
    setToken('');
    localStorage.removeItem('user');
    localStorage.removeItem('token');
  };

  return (
    <AuthContext.Provider value={{ user, token, login, register, logout, authLoading, authError }}>
      {children}
    </AuthContext.Provider>
  );
};
