import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => (
  <div className="flex flex-col items-center justify-center min-h-[70vh] bg-gradient-to-br from-white to-gray-100 font-sans animate-fade-in">
    <h1 className="text-5xl font-extrabold text-primary mb-6 text-center drop-shadow-lg">SmartCart: Intelligent Pricing at Your Fingertips</h1>
    <p className="text-xl text-gray-600 mb-10 text-center max-w-2xl">Shop smarter with real-time discounts, loyalty rewards, and a beautiful, transparent cart experience. Enjoy a seamless, modern shopping journey!</p>
    <Link to="/products" className="bg-primary text-white px-10 py-4 rounded-2xl text-2xl font-bold shadow-lg hover:bg-red-600 transition-all duration-200">Browse Products</Link>
  </div>
);

export default Home;
