import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { cart } = useCart();
  const { user, logout } = useAuth();
  const cartCount = cart.reduce((sum, item) => sum + item.quantity, 0);
  const navigate = useNavigate();

  return (
    <nav className="bg-blue-700 text-white px-4 py-3 flex justify-between items-center shadow-md">
      <Link to="/" className="font-bold text-xl">Smart Cart</Link>
      <div className="flex items-center gap-4">
        <Link to="/cart" className="hover:underline flex items-center">
          Cart
          {cartCount > 0 && (
            <span className="ml-2 bg-red-500 text-white rounded-full px-2 py-0.5 text-xs font-bold animate-bounce" aria-label={`Cart has ${cartCount} items`}>
              {cartCount}
            </span>
          )}
        </Link>
        {user && (
          <Link to="/orders" className="hover:underline">Orders</Link>
        )}
        {!user ? (
          <>
            <Link to="/login" className="hover:underline">Login</Link>
            <Link to="/register" className="hover:underline">Register</Link>
          </>
        ) : (
          <div className="flex items-center gap-2">
            <span className="font-semibold">{user.name}</span>
            <button
              className="bg-blue-900 px-2 py-1 rounded hover:bg-blue-800"
              onClick={() => { logout(); navigate('/'); }}
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
