import React, { useState } from 'react';

const ProductList = ({ products, onAddToCart }) => {
  const [quantities, setQuantities] = useState({});
  const [modalProduct, setModalProduct] = useState(null);
  const [search, setSearch] = useState('');

  const handleQuantityChange = (id, value) => {
    setQuantities(q => ({ ...q, [id]: Math.max(1, Number(value)) }));
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(search.toLowerCase()) ||
    product.category.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="p-4 w-full bg-gray-50 rounded-xl shadow-none">
      <h2 className="text-2xl font-extrabold mb-6 tracking-tight">Products</h2>
      <input
        type="text"
        placeholder="Search products..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="mb-6 w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-[#2874f0]"
        aria-label="Search products"
      />
      {filteredProducts.length === 0 && <div className="text-gray-400">No products found.</div>}
      <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {filteredProducts.map((product, idx) => (
          <li
            key={product.id}
            className="bg-white rounded-xl border border-gray-200 shadow-md p-4 flex flex-col items-center hover:shadow-2xl transition-shadow duration-300 transform hover:-translate-y-1 animate-fade-in group relative overflow-hidden"
          >
            {/* Sale badge for first product as demo */}
            {idx === 0 && (
              <span className="absolute top-4 left-4 bg-gradient-to-r from-[#ff9f00] to-[#fb641b] text-white px-3 py-1 rounded-full text-xs font-bold shadow animate-fade-in-up">SALE</span>
            )}
            <div className="w-full h-48 flex items-center justify-center mb-3 overflow-hidden rounded-xl relative">
              <img
                src={product.image}
                alt={product.name}
                className="max-h-40 object-contain transition-transform duration-300 group-hover:scale-110"
                style={{ background: 'linear-gradient(135deg, #f8f8f8 60%, #e3f0ff 100%)' }}
                onClick={() => setModalProduct(product)}
              />
              {/* View Details button on hover */}
              <button
                className="absolute bottom-2 right-2 bg-[#2874f0] text-white px-3 py-1 rounded-lg text-xs font-semibold opacity-0 group-hover:opacity-100 transition-opacity duration-200 shadow-lg"
                onClick={() => setModalProduct(product)}
              >
                View Details
              </button>
            </div>
            <div className="font-semibold text-lg mb-1 text-center">{product.name}</div>
            <div className="text-gray-500 mb-1">{product.category}</div>
            <div className="text-[#ff9f00] font-bold text-xl mb-2">${product.price.toFixed(2)}</div>
            <div className="text-xs text-gray-400 mb-2">Stock: {product.stock}</div>
            <div className="flex items-center gap-2 mt-2 w-full">
              <input
                type="number"
                min={1}
                value={quantities[product.id] || 1}
                onChange={e => handleQuantityChange(product.id, e.target.value)}
                className="w-16 border rounded px-2 py-1"
                disabled={product.stock === 0}
              />
              <button
                className={`flex-1 px-4 py-2 rounded-lg font-semibold transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-[#ff9f00] ${
                  product.stock === 0
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    : 'bg-gradient-to-r from-[#ff9f00] to-[#fb641b] text-white hover:from-[#fb641b] hover:to-[#ff9f00] shadow-lg'
                }`}
                onClick={() => onAddToCart(product, quantities[product.id] || 1)}
                disabled={product.stock === 0}
              >
                {product.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
              </button>
            </div>
            {/* Modal for View Details */}
            {modalProduct && modalProduct.id === product.id && (
              <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50 animate-fade-in-up">
                <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-md w-full relative">
                  <button className="absolute top-2 right-2 text-gray-400 hover:text-red-500 text-2xl font-bold" onClick={() => setModalProduct(null)}>&times;</button>
                  <img src={modalProduct.image} alt={modalProduct.name} className="w-40 h-40 object-contain mx-auto mb-4 rounded-xl" />
                  <div className="font-bold text-2xl mb-2 text-center">{modalProduct.name}</div>
                  <div className="text-gray-500 mb-2 text-center">{modalProduct.category}</div>
                  <div className="text-[#ff9f00] font-bold text-xl mb-4 text-center">${modalProduct.price.toFixed(2)}</div>
                  <div className="text-gray-700 mb-4 text-center">
                    {modalProduct.id === 1 && (
                      <>Powerful and portable laptop with a high-resolution display, fast SSD storage, and long battery life. Perfect for work, study, or entertainment on the go.</>
                    )}
                    {modalProduct.id === 2 && (
                      <>A bestselling book that will inspire and entertain. Dive into a world of adventure, knowledge, or self-discovery with this must-read title for all ages.</>
                    )}
                    {modalProduct.id === 3 && (
                      <>Classic crew-neck t-shirt made from soft, breathable cotton. Comfortable fit and durable design, ideal for everyday wear in any season.</>
                    )}
                    {[1,2,3].indexOf(modalProduct.id) === -1 && (
                      <>No additional details available for this product.</>
                    )}
                  </div>
                  <button className="w-full bg-[#2874f0] text-white py-3 rounded-lg font-bold hover:bg-[#ff9f00] transition" onClick={() => { onAddToCart(modalProduct, 1); setModalProduct(null); }}>Add to Cart</button>
                </div>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductList;
