import React from 'react';
import CartItem from './CartItem';

const Cart = ({ cart, onRemove, onQuantityChange }) => {
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  return (
    <div className="p-4 w-full md:w-1/2 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Shopping Cart</h2>
      {cart.length === 0 ? (
        <div className="flex flex-col items-center justify-center py-8">
          <svg width="64" height="64" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="text-gray-300 mb-2"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2 9m5-9v9m4-9v9m4-9l2 9" /></svg>
          <div className="text-gray-400">Your cart is empty.</div>
        </div>
      ) : (
        <table className="w-full text-sm mb-4">
          <thead>
            <tr>
              <th>Product</th>
              <th>Category</th>
              <th>Price</th>
              <th>Qty</th>
              <th>Subtotal</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {cart.map(item => (
              <CartItem key={item.id} item={item} onRemove={onRemove} onQuantityChange={onQuantityChange} />
            ))}
          </tbody>
        </table>
      )}
      <div className="font-semibold">Subtotal: ${subtotal.toFixed(2)}</div>
    </div>
  );
};

export default Cart;
