import React, { useState } from 'react';

const ProductList = ({ products, onAddToCart }) => {
  const [quantities, setQuantities] = useState({});
  const [search, setSearch] = useState('');

  const handleQuantityChange = (id, value) => {
    setQuantities(q => ({ ...q, [id]: Math.max(1, Number(value)) }));
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(search.toLowerCase()) ||
    product.category.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="p-4 w-full bg-gray-50 rounded-xl shadow-none">
      <h2 className="text-2xl font-extrabold mb-6 tracking-tight">Products</h2>
      <input
        type="text"
        placeholder="Search products..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="mb-6 w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
        aria-label="Search products"
      />
      {filteredProducts.length === 0 && <div className="text-gray-400">No products found.</div>}
      <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {filteredProducts.map(product => (
          <li key={product.id} className="bg-white rounded-xl shadow-lg p-6 flex flex-col items-center hover:shadow-xl transition-shadow animate-fade-in">
            <div className="text-5xl mb-2">{product.image || 'ðŸ›’'}</div>
            <div className="font-bold text-lg mb-1">{product.name}</div>
            <div className="text-gray-500 mb-1">{product.category}</div>
            <div className="text-primary font-semibold mb-2">${product.price.toFixed(2)}</div>
            <div className="text-xs text-gray-400 mb-2">Stock: {product.stock}</div>
            <div className="flex items-center gap-2 mt-2 w-full">
              <input
                type="number"
                min={1}
                value={quantities[product.id] || 1}
                onChange={e => handleQuantityChange(product.id, e.target.value)}
                className="w-16 border rounded px-2 py-1"
                disabled={product.stock === 0}
              />
              <button
                className={`flex-1 px-4 py-2 rounded-lg font-semibold transition-all duration-200 ${
                  product.stock === 0
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    : 'bg-gradient-to-r from-primary to-red-600 text-white hover:from-red-600 hover:to-primary'
                }`}
                onClick={() => onAddToCart(product, quantities[product.id] || 1)}
                disabled={product.stock === 0}
              >
                {product.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductList;
