import React, { useEffect, useState } from 'react';
import api from '../services/axios';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    setLoading(true);
    api.get('/orders')
      .then(res => setOrders(res.data))
      .catch(() => setError('Failed to fetch orders'))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Order History</h2>
      {loading ? (
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-blue-600 border-solid"></div>
        </div>
      ) : error ? (
        <div className="text-red-600">{error}</div>
      ) : orders.length === 0 ? (
        <div className="text-gray-400">No orders found.</div>
      ) : (
        <div className="space-y-6">
          {orders.map(order => (
            <div key={order._id} className="bg-white rounded shadow p-4">
              <div className="mb-2 text-sm text-gray-500">Order Date: {new Date(order.createdAt).toLocaleString()}</div>
              <table className="w-full text-sm mb-2">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Category</th>
                    <th>Price</th>
                    <th>Qty</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {order.items.map(item => (
                    <tr key={item.productId}>
                      <td>{item.name}</td>
                      <td>{item.category}</td>
                      <td>${item.price.toFixed(2)}</td>
                      <td>{item.quantity}</td>
                      <td>${item.subtotal.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <div className="mb-1">Subtotal: ${order.subtotal.toFixed(2)}</div>
              <div className="mb-1 text-green-700">Bulk Discount: -${order.bulkDiscount.toFixed(2)}</div>
              <div className="mb-1 text-green-700">Loyalty Discount: -${order.loyaltyDiscount.toFixed(2)}</div>
              <div className="font-bold text-lg mt-2">Final Total: ${order.finalTotal.toFixed(2)}</div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Orders;
