import React from 'react';
import Cart from '../components/Cart';
import CartSummary from '../components/CartSummary';

const CartPage = ({ cart, onRemove, onQuantityChange, clearCart, loyaltyLevel, setLoyaltyLevel }) => {
  return (
    <div className="max-w-6xl mx-auto p-4 flex flex-col md:flex-row gap-8 animate-fade-in">
      <div className="flex-1">
        <Cart cart={cart} onRemove={onRemove} onQuantityChange={onQuantityChange} />
      </div>
      <div className="w-full md:w-[400px]">
        <CartSummary cart={cart} clearCart={clearCart} loyaltyLevel={loyaltyLevel} setLoyaltyLevel={setLoyaltyLevel} />
      </div>
    </div>
  );
};

export default CartPage;
