import { motion, AnimatePresence } from 'framer-motion';
import CartItem from './CartItem';

const Cart = ({ cart, onRemove, onQuantityChange }) => {
  const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
  return (
    <motion.div layout className="p-6 w-full bg-white/80 backdrop-blur-lg rounded-3xl shadow-xl border border-gray-100 animate-fade-in">
      <h2 className="text-3xl font-extrabold mb-6 tracking-tight">Shopping Cart</h2>
      <AnimatePresence>
        {cart.length === 0 ? (
          <motion.div
            key="empty"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 40 }}
            className="flex flex-col items-center justify-center py-12"
          >
            <div className="w-40 h-40 mb-4 flex items-center justify-center">
              <svg width="120" height="120" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="text-gray-300">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h2l.4 2M7 13h10l4-8H5.4M7 13L5.4 5M7 13l-2 9m5-9v9m4-9v9m4-9l2 9" />
              </svg>
            </div>
            <div className="text-gray-400 text-lg font-semibold">Your cart is empty.</div>
          </motion.div>
        ) : (
          <motion.div
            key="table"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 40 }}
            className="overflow-x-auto"
          >
            <table className="w-full text-base mb-4">
              <thead>
                <tr className="bg-gray-50">
                  <th className="py-2 px-3 text-left">Product</th>
                  <th className="py-2 px-3 text-left">Category</th>
                  <th className="py-2 px-3 text-left">Price</th>
                  <th className="py-2 px-3 text-left">Qty</th>
                  <th className="py-2 px-3 text-left">Subtotal</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                <AnimatePresence initial={false}>
                  {cart.map(item => (
                    <motion.tr
                      key={item.id}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: 20 }}
                      transition={{ duration: 0.2 }}
                    >
                      <CartItem item={item} onRemove={onRemove} onQuantityChange={onQuantityChange} />
                    </motion.tr>
                  ))}
                </AnimatePresence>
              </tbody>
            </table>
          </motion.div>
        )}
      </AnimatePresence>
      <div className="font-bold text-xl mt-6 flex justify-between">
        <span>Subtotal:</span> <span>${subtotal.toFixed(2)}</span>
      </div>
    </motion.div>
  );
};

export default Cart;
