import React, { useState } from 'react';

const CartSummary = ({ pricingDetails, clearCart }) => {
  const [showModal, setShowModal] = useState(false);
  const [animate, setAnimate] = useState(false);

  React.useEffect(() => {
    if (pricingDetails) {
      setAnimate(true);
      const timeout = setTimeout(() => setAnimate(false), 600);
      return () => clearTimeout(timeout);
    }
  }, [pricingDetails?.finalTotal]);

  if (!pricingDetails) return null;
  return (
    <div className="mt-4 p-4 bg-gray-50 rounded shadow">
      <div className="mb-1">Subtotal: <span className="font-semibold">${pricingDetails.subtotal.toFixed(2)}</span></div>
      <div className="mb-1 text-green-700">Bulk Discount: -${pricingDetails.bulkDiscount.toFixed(2)}</div>
      <div className="mb-1 text-green-700">Loyalty Discount: -${pricingDetails.loyaltyDiscount.toFixed(2)}</div>
      <div className={`font-bold text-lg mt-2 transition-all duration-500 ${animate ? 'bg-green-100 scale-105 px-2 py-1 rounded' : ''}`}>Final Total: ${pricingDetails.finalTotal.toFixed(2)}</div>
      <button
        className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        onClick={() => setShowModal(true)}
      >
        Clear Cart
      </button>
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-lg max-w-xs w-full">
            <div className="mb-4 text-lg">Are you sure you want to clear the cart?</div>
            <div className="flex justify-end gap-2">
              <button className="px-4 py-2 rounded bg-gray-200" onClick={() => setShowModal(false)}>Cancel</button>
              <button className="px-4 py-2 rounded bg-red-600 text-white" onClick={() => { clearCart(); setShowModal(false); }}>Clear</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartSummary;
