import React, { useState } from 'react';
import Cart from '../components/Cart';
import CartSummary from '../components/CartSummary';
import { useCart } from '../context/CartContext';
import api from '../services/axios';

const CartPage = () => {
  const { cart, loyaltyLevel, pricingDetails, setPricingDetails, clearCart } = useCart();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleCheckout = async () => {
    setLoading(true);
    setError('');
    try {
      const items = cart.map(({ productId, quantity }) => ({ productId, quantity }));
      const res = await api.post('/cart', { items, loyaltyLevel });
      setPricingDetails(res.data);
    } catch (err) {
      setError('Checkout failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <Cart cart={cart} pricingDetails={pricingDetails} />
      <CartSummary pricingDetails={pricingDetails} clearCart={clearCart} />
      <button
        className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full"
        onClick={handleCheckout}
        disabled={cart.length === 0 || loading}
      >
        {loading ? 'Processing...' : 'Checkout'}
      </button>
      {error && <div className="text-red-600 mt-2">{error}</div>}
    </div>
  );
};

export default CartPage;
