import React, { useState } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { FiShoppingCart, FiSearch } from 'react-icons/fi';

const Navbar = () => {
  const location = useLocation();
  const [search, setSearch] = useState('');
  // For demo, cart count is not shown. You can pass cart count as prop if needed.
  return (
    <nav className="bg-[#2874f0] text-white sticky top-0 z-50 shadow-md w-full font-sans">
      <div className="max-w-7xl mx-auto flex items-center justify-between px-6 py-3">
        {/* Logo */}
        <NavLink to="/" className="font-extrabold text-2xl tracking-tight flex items-center gap-2">
          <span className="text-white">SmartCart</span>
        </NavLink>
        {/* Search Bar */}
        <div className="flex-1 flex justify-center mx-6">
          <div className="relative w-full max-w-md">
            <input
              type="text"
              placeholder="Search for products, brands and more"
              className="w-full px-4 py-2 rounded-l-lg text-black focus:outline-none"
              value={search}
              onChange={e => setSearch(e.target.value)}
            />
            <button className="absolute right-0 top-0 h-full px-4 bg-[#ff9f00] rounded-r-lg text-white hover:bg-[#fb641b] transition">
              <FiSearch size={20} />
            </button>
          </div>
        </div>
        {/* Nav Links & Cart */}
        <div className="flex items-center gap-6">
          <NavLink to="/products" className={({isActive}) => isActive ? 'text-[#ff9f00] font-bold' : 'hover:text-[#ff9f00]'}>Products</NavLink>
          <NavLink to="/cart" className={({isActive}) => isActive ? 'text-[#ff9f00] font-bold flex items-center' : 'hover:text-[#ff9f00] flex items-center'}>
            <FiShoppingCart size={22} className="mr-1" /> Cart
          </NavLink>
          <NavLink to="/loyalty" className={({isActive}) => isActive ? 'text-[#ff9f00] font-bold' : 'hover:text-[#ff9f00]'}>Loyalty</NavLink>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
