import React from 'react';

const badgeColors = {
  Bronze: 'bg-yellow-800 text-white',
  Silver: 'bg-gray-400 text-white',
  Gold: 'bg-yellow-400 text-black',
};

const LoyaltyPage = ({ loyaltyLevel, setLoyaltyLevel }) => (
  <div className="flex flex-col items-center justify-center min-h-[60vh] bg-white font-sans">
    <h2 className="text-2xl font-bold mb-4">Select Loyalty Level</h2>
    <select
      className="border rounded px-4 py-2 text-lg mb-4"
      value={loyaltyLevel}
      onChange={e => setLoyaltyLevel(e.target.value)}
    >
      <option value="Bronze">Bronze</option>
      <option value="Silver">Silver</option>
      <option value="Gold">Gold</option>
    </select>
    <span className={`px-4 py-2 rounded-full font-bold ${badgeColors[loyaltyLevel]}`}>{loyaltyLevel}</span>
  </div>
);

export default LoyaltyPage;
