import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const { register, authLoading, authError } = useAuth();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loyaltyLevel, setLoyaltyLevel] = useState('Bronze');
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    const success = await register(name, email, password, loyaltyLevel);
    if (success) navigate('/');
  };

  return (
    <div className="max-w-sm mx-auto mt-12 p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Register</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={e => setName(e.target.value)}
          className="w-full mb-2 px-3 py-2 border rounded"
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          className="w-full mb-2 px-3 py-2 border rounded"
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          className="w-full mb-2 px-3 py-2 border rounded"
          required
        />
        <select
          value={loyaltyLevel}
          onChange={e => setLoyaltyLevel(e.target.value)}
          className="w-full mb-2 px-3 py-2 border rounded"
        >
          <option value="Bronze">Bronze</option>
          <option value="Silver">Silver</option>
          <option value="Gold">Gold</option>
        </select>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded mt-2 hover:bg-blue-700"
          disabled={authLoading}
        >
          {authLoading ? 'Registering...' : 'Register'}
        </button>
        {authError && <div className="text-red-600 mt-2">{authError}</div>}
      </form>
    </div>
  );
};

export default Register;
