import React from 'react';

const badgeColors = {
  Bronze: 'bg-yellow-800 text-white',
  Silver: 'bg-gray-400 text-white',
  Gold: 'bg-yellow-400 text-black',
};

const LoyaltyPage = ({ loyaltyLevel, setLoyaltyLevel }) => (
  <div className="flex flex-col items-center justify-center min-h-[70vh] bg-gradient-to-br from-white to-gray-100 font-sans animate-fade-in">
    <div className="bg-white rounded-2xl shadow-lg p-10 flex flex-col items-center">
      <h2 className="text-3xl font-extrabold mb-6">Select Loyalty Level</h2>
      <select
        className="border-2 border-primary rounded px-6 py-3 text-xl mb-6 focus:ring-2 focus:ring-primary"
        value={loyaltyLevel}
        onChange={e => setLoyaltyLevel(e.target.value)}
      >
        <option value="Bronze">Bronze</option>
        <option value="Silver">Silver</option>
        <option value="Gold">Gold</option>
      </select>
      <span className={`px-8 py-3 rounded-full font-bold text-xl shadow ${badgeColors[loyaltyLevel]}`}>{loyaltyLevel}</span>
    </div>
  </div>
);

export default LoyaltyPage;
