import React, { useState, useEffect } from 'react';

function calculateTax(category, price) {
  if (category === 'Electronics') return price * 0.10;
  if (category === 'Clothing') return price * 0.05;
  return 0;
}
function getLoyaltyDiscount(level) {
  if (level === 'Gold') return 0.15;
  if (level === 'Silver') return 0.10;
  if (level === 'Bronze') return 0.05;
  return 0;
}

const CartSummary = ({ cart, clearCart, loyaltyLevel, setLoyaltyLevel }) => {
  let itemized = [];
  let subtotal = 0;
  cart.forEach(item => {
    let base = item.price * item.quantity;
    let discount = 0;
    if (item.category === 'Electronics' && item.quantity > 2) {
      discount = base * 0.15;
      base -= discount;
    }
    const tax = calculateTax(item.category, base);
    const subtotalItem = base + tax;
    subtotal += subtotalItem;
    itemized.push({
      ...item,
      tax,
      subtotal: subtotalItem,
      itemDiscount: discount
    });
  });
  let bulkDiscount = subtotal > 200 ? subtotal * 0.10 : 0;
  let afterBulk = subtotal - bulkDiscount;
  let loyaltyDiscount = afterBulk * getLoyaltyDiscount(loyaltyLevel);
  let finalTotal = afterBulk - loyaltyDiscount;

  // Animate final total
  const [animate, setAnimate] = useState(false);
  useEffect(() => {
    setAnimate(true);
    const timeout = setTimeout(() => setAnimate(false), 600);
    return () => clearTimeout(timeout);
  }, [finalTotal]);

  return (
    <div className="mt-4 p-6 bg-white rounded-2xl shadow-lg max-w-md mx-auto animate-fade-in">
      <div className="mb-1 flex justify-between"><span>Subtotal:</span> <span className="font-semibold">${subtotal.toFixed(2)}</span></div>
      <div className="mb-1 flex justify-between text-green-700"><span>Bulk Discount:</span> <span>- ${bulkDiscount.toFixed(2)}</span></div>
      <div className="mb-1 flex justify-between text-green-700"><span>Loyalty Discount:</span> <span>- ${loyaltyDiscount.toFixed(2)}</span></div>
      <div className={`font-bold text-xl mt-4 flex justify-between transition-all duration-500 ${animate ? 'bg-green-100 scale-105 px-2 py-1 rounded' : ''}`}>
        <span>Final Total:</span> <span>${finalTotal.toFixed(2)}</span>
      </div>
      <button
        className="mt-6 bg-red-600 text-white px-6 py-3 rounded-lg hover:bg-primary w-full font-bold text-lg transition"
        onClick={clearCart}
      >
        Clear Cart
      </button>
      <div className="mt-6 flex items-center gap-2">
        <label className="font-semibold mr-2">Loyalty Level:</label>
        <select
          className="border rounded px-2 py-1"
          value={loyaltyLevel}
          onChange={e => setLoyaltyLevel(e.target.value)}
        >
          <option value="Bronze">Bronze</option>
          <option value="Silver">Silver</option>
          <option value="Gold">Gold</option>
        </select>
      </div>
    </div>
  );
};

export default CartSummary;
