import React from 'react';
import { NavLink } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();
  return (
    <nav className="bg-white shadow sticky top-0 z-50 flex items-center justify-between px-8 py-4 font-sans">
      <span className="font-extrabold text-2xl text-primary">SmartCart</span>
      <div className="flex gap-6 items-center">
        <NavLink to="/" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Home</NavLink>
        <NavLink to="/products" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Products</NavLink>
        <NavLink to="/cart" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Cart</NavLink>
        <NavLink to="/loyalty" className={({isActive}) => isActive ? 'text-primary font-bold' : 'hover:text-primary'}>Loyalty</NavLink>
      </div>
    </nav>
  );
};

export default Navbar;
